INSERT INTO `s_category` VALUES ('33398E13FFC54EA4806D5CBBF60AF360', 's1号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '南京至高淳城际快速轨道南京南站至禄口机场段（简称S1号线）南起禄口机场，经禄口新城、东善桥～秣陵片区、东山副城西侧，止于南京南站', '5'), ('365B36E856F9417A8C4A39405B0FB5CF', 's8号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '南京至天长城际快速轨道泰山新村至金牛湖段（简称S8号线）南起泰山新村站，经过桥北地区、浦口沿江、大厂、六合，北至金牛湖站，途径浦口区和六合 区', '6'), ('52767911A81645F68088652BD81CC37A', '3号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '3号线全长44.9公里，线路途径浦口区、鼓楼区、玄武区、秦淮区、雨花台区和江宁区。共设置29座车站，其中林场站为高架站，其余28座车站均为地下站', '3'), ('A2912C5040E94285A87E158B26190011', '2号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '南京地铁2号线按建设启动时间分为一期工程和东延工程两段。2号线一期起于河西新区的油坊桥，止于紫金山麓的马群，线路贯穿南京主城区的东西向中轴线', '2'), ('C8C39AF1A00B44FDA32B5AC3C020ECD6', '4号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '地铁4号线是南京城市轨道交通线网中的一条骨干线，线路横贯南京东西，一期工程起自草场门大街与江东中路交汇处的龙江站', '8'), ('CD64A72D1FEF4E51BB7EF188F8E19545', '南京', null, '江苏南京', '7'), ('D513BD22B68C4A89B11AB91909ABC250', '1号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '地铁1号线途径栖霞区、鼓楼区、玄武区、秦淮区、雨花台区和江宁区，线路北起迈皋桥站，沿南京主城区中轴线一路南下', '1'), ('F2F3FD598A07439BAE56D0D39CFCD45F', '10号线', 'CD64A72D1FEF4E51BB7EF188F8E19545', '地铁10号线途径浦口区、建邺区和雨花台区，线路西起雨山路站，跨越长江进入河西新城，东至安德门站，线路总长21.6千米', '4');
COMMIT;

